context.value:
@include font-size(-1);
  background: $colour-panel-dark;
  color: $colour-text-reverse;
  margin-top: em(32px);
  padding: em(32px 0);

  @media print {
    display: none;
  }

  a {
    color: $colour-text-reverse;

    &:focus {
      color: #000;
    }
  }

  p {
    @include font-size(-1);
  }

  &__logo {
    display: block;
    margin: em(0 0 32 0);
    text-decoration: none;

    .icon {
      font-size: rem(48px);
    }
  }

  &__services {
    margin: em(0 0 32 0);
  }

  &__social {
    clear: both;
    margin: em(0 0 32 0);
  }

  &__nav {
    clear: both;
    margin: em(0 0 32 0);
  }

  &__copyright {
    margin: 0;
    text-align: center;
  }

  @include mq($from: sm) {
    &__logo {
      float: left;
      margin: em(0 32 32 0);
    }

    &__services {
      float: left;
      margin: em(0 0 32 0);
    }

    &__social {
      clear: both;
    }

    &__copyright {
      text-align: left;
    }
  }

  @include mq($from: md) {

    &__social {
      clear: none;
      float: right;
    }
  }

&__list {
    list-style: none;
    margin: 0;
    overflow: hidden;
    padding: 0;
  }

  &__item {
    border-bottom: 1px solid $colour-border;

    &:first-child {
      border-top: 1px solid $colour-border;
    }

    a {
      @include font-size(-1);
      color: $colour-panel-light;
      display: block;
      padding: em(8 0);
      text-decoration: none;

      &:hover {
        color: $colour-text-reverse;
      }

      &:focus {
        color: $colour-text;
      }
    }

    .icon {
      @include font-size(1);
      display: inline-block;
      margin: em(0 4 0 0);
      vertical-align: middle;
    }
  }

  @include mq($from: sm) {

    &__item {
      border: 0;
      float: left;
      text-align: center;
      width: em(90px);

      &:first-child {
        border: 0;
      }

      a {
        padding: em(4px) 0 0;
      }
    }

    .icon {
      @include font-size(6);
      display: block;
      line-height: 1;
      margin: 0 auto em(4px);

      // The following is needed for IE8 to go centrally aligned
      &:before {
        text-align: center;
        width: 100%;
      }
    }
  }

&__heading {
    @include font-size(-1);
    display: inline-block;
    font-weight: normal;
    margin: em(0 8 0 0);
    padding: 0;
    vertical-align: top;
  }

  &__list {
    display: inline;
    list-style: none;
    margin: 0;
    padding: 0;
  }

  &__item {
    display: inline-block;

    .icon {
      @include font-size(4);
      line-height: 1;
    }
  }

  a {
    display: block;
    padding: em(0 4);
    text-decoration: none;

    &:focus,
    &:active {
      color: $colour-text;
    }
  }

&__list {
    list-style: none;
    margin: 0;
    padding: 0;
  }

  a {
    padding: em(4 0);
    text-decoration: none;
  }

  @include mq($from: sm) {
    &__list {
      line-height: 1;
    }

    &__item {
      border-left: 1px solid $colour-text-reverse;
      display: inline-block;
      line-height: 1;

      &:first-child {
        border-left: 0;

        a {
          padding-left: 0;
        }
      }
    }

    a {
      padding: em(0 8);
    }
  }

// The list of tabs
  &__list {
    list-style: none;
    margin: 0;
    overflow: hidden;
    padding: 0;
  }

  //
  &__tab {
    float: left;

    &--active .tabs__tab-btn { // sass-lint:disable-line no-combinators
      background: $colour-panel-light;
    }
  }

  // The button/link for the tab itself
  &__tab-btn {
    background: 0;
    border: 0;
    cursor: pointer;
    display: block;
    line-height: 4rem;
    margin: 0;
    padding: 0 1rem;

    &:hover {
      background: $colour-panel-light;
    }
  }

  // The tab content containing the tab panels
  &__content {
    background: $colour-panel-light;
    padding: 1rem;
  }

  // An individual tab content pane
  &__pane {

    // Assume tabs will stack if no JS is available
    .js & {
      display: none;

      &--active {
        display: block;
      }
    }
  }

@include font-size(0);
  list-style: none;
  margin: em(0 0 $spacing-large);
  padding: 0;

  &__crumb {
    display: inline-block;

    &:before {
      @include nice-icon(angle-right);
      margin: em(0 $spacing-x-small);
    }

    &:first-child:before {
      display: none;
    }

    a {
      color: $colour-text;
    }
  }

@include remove-mz-focus-inner;
  @include font-size(0);
  appearance: none;
  background: $colour-btn-primary;
  border: em(1px) solid $colour-btn-primary-text;
  border-radius: 0;
  color: $colour-btn-primary-text;
  cursor: pointer;
  display: inline-block;
  margin: em(0 $spacing-x-small 0 0);
  padding: em($spacing-small $spacing-medium);
  position: relative;
  text-decoration: none;
  vertical-align: top;

  &:visited {
    color: $colour-btn-primary-text;
  }

  &:hover {
    background: $colour-btn-primary-hover;
    color: $colour-btn-primary-text;
    text-decoration: none;
  }

  &:focus {
    @include default-focus-style;
    background: $colour-btn-primary;
    border-color: $colour-focus;
  }

  &:active {
    background: $colour-btn-primary-hover;
    text-decoration: none;
    top: em(2px);
  }

  // Disabled button styles
  &--disabled,
  &.disabled,
  &[aria-disabled='true'],
  &[disabled='disabled'],
  &[disabled] {
    opacity: .5;
    pointer-events: none;

    &:hover {
      cursor: default;
    }
  }

  // CTA/prominent button
  &--cta {
    background: $colour-btn-cta;
    border: em(1px) solid $colour-btn-cta-text;
    color: $colour-btn-cta-text;

    &:visited {
      color: $colour-btn-cta-text;
    }

    &:hover {
      background: $colour-btn-cta-hover;
      color: $colour-btn-cta-text;
    }

    &:focus {
      background: $colour-btn-cta-hover;
    }

    &:active {
      background: $colour-btn-cta-hover;
    }
  }

  // Secondary button
  &--secondary {
    background: $colour-btn-secondary;
    border: em(1px) solid $colour-btn-secondary-text;
    color: $colour-btn-secondary-text;

    &:visited {
      color: $colour-btn-secondary-text;
    }

    &:hover {
      background: $colour-btn-secondary-hover;
      color: $colour-btn-secondary-text;
    }

    &:focus {
      background: $colour-btn-secondary-hover;
    }

    &:active {
      background: $colour-btn-secondary-hover;
    }
  }

  // Light button, for use on dark panels/sections
  &--light {
    background: $colour-btn-light;
    border: em(1px) solid $colour-btn-light-text;
    color: $colour-btn-light-text;

    &:visited {
      color: $colour-btn-light-text;
    }

    &:hover {
      background: $colour-btn-light-hover;
      color: $colour-btn-light-text;
    }

    &:focus {
      background: $colour-btn-light-hover;
    }

    &:active {
      background: $colour-btn-light-hover;
    }
  }

context.code:
margin: auto;
max-width: 1170px;
width: 96%;

context.value:
@extend %container;

  &--narrow {
    max-width: 1000px;
  }

  &--full {
    max-width: 100%;
    padding: 0 2%;
    width: auto;
  }

  &--panel {
    background: $colour-panel-light;
    padding: 0 2%;
    width: auto;
  }

display: block;
  height: 0;
  overflow: hidden;
  padding: 0;
  position: relative;

  &__item,
  iframe,
  embed,
  object,
  video {
    border: 0;
    bottom: 0;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
  }

  // Modifier class for 21:9 aspect ratio
  &--21-9 {
    padding-bottom: percentage(9 / 21);
  }

  // Modifier class for 16:9 aspect ratio
  &--16-9 {
    padding-bottom: percentage(9 / 16);
  }

  // Modifier class for 4:3 aspect ratio
  &--4-3 {
    padding-bottom: percentage(3 / 4);
  }

  // Modifier class for square aspect ratio
  &--square {
    padding-bottom: 100%;
  }

border-bottom: 1px solid $colour-border;
