context.value:
@include links-inverse;
  background: $colour-nice-cool-teal;
  color: $colour-text-inverse;
  margin: em($spacing-large 0);
  padding: em($spacing-small 0);
  position: relative;

  &__container {
    @extend %container;
  }

  &__inner {
    @include grid;
  }

  &__text {
    @include grid-item($middle: true);
    margin-bottom: em($spacing-medium);
  }

  &__title {
    @include font-size(3);
    color: $colour-text-inverse;
    margin: em(0 0 $spacing-x-small);
  }

  &__intro {
    margin: 0;
  }

  &__actions {
    @include grid-item($middle: true);

    .btn {
      margin: 0;
    }
  }

  &__close {
    appearance: none;
    background: 0;
    border: 0;
    color: $colour-text-inverse;
    cursor: pointer;
    line-height: 1;
    padding: rem($spacing-small);
    position: absolute;
    right: 0;
    top: 0;
    z-index: 1;

    &:hover {
      background: rgba(0, 0, 0, .2);
    }

    &:focus {
      @include default-focus-style;
    }
  }

  &--subtle {
    @include links-default;
    background: $colour-panel-default;
    color: $colour-text;

    .action-banner__title {
      color: $colour-text;
    }

    .action-banner__close {
      color: $colour-text;
    }
  }

  &--closeable {
    .action-banner__title {
      padding-right: rem($spacing-large); // Make toom for close button
    }
  }

  @include mq($from: md) {
    &__text {
      margin-bottom: 0;
      width: percentage(3/4);
    }

    &__actions {
      text-align: right;
      width: percentage(1/4);
    }

    &--closeable {
      .action-banner__title {
        padding-right: 0;
      }

      .action-banner__actions {
        padding-right: rem($spacing-medium);
      }
    }
  }

  @media print {
    @include important(display, none);
  }

@include font-size(0);
  list-style: none;
  margin: em($spacing-small 0 $spacing-large);
  padding: 0;

  &__crumb {
    display: inline-block;

    &:before {
      content: get-glyph(greater-than);
      font-size: 80%;
      margin: em(0 $spacing-x-small);
    }

    &:first-child:before {
      display: none;
    }
  }

  a {
    color: $colour-text;
    text-decoration: underline;

    &:hover {
      text-decoration: none;
    }

    &:focus {
      color: $colour-link-focus-text;
    }

    &:active {
      color: $colour-link-active;
    }
  }

@include remove-mz-focus-inner;
  @include font-size(0);
  appearance: none;
  background: $colour-btn-primary;
  border: em(1px) solid $colour-btn-primary-text;
  border-radius: 0;
  color: $colour-btn-primary-text;
  cursor: pointer;
  display: inline-block;
  margin: em(0 $spacing-small $spacing-small -4px); // Negative left margin to combat inline-block gaps
  padding: em($spacing-small $spacing-medium);
  position: relative;
  text-align: left;
  text-decoration: none !important; // sass-lint:disable-line no-important
  vertical-align: top;
  white-space: nowrap;

  &:first-child {
    margin-left: 0;
  }

  &:visited {
    color: $colour-btn-primary-text;
  }

  &:hover {
    background: $colour-btn-primary-hover;
    color: $colour-btn-primary-text;
    text-decoration: none;
  }

  &:focus {
    @include default-focus-style;
    background: $colour-btn-primary;
    border-color: $colour-focus-inverse;
    color: $colour-btn-primary-text;
  }

  &:active {
    background: $colour-btn-primary-hover;
    text-decoration: none;
    top: em(2px);
  }

  // Disabled button styles
  &--disabled,
  &.disabled,
  &[aria-disabled='true'],
  &[disabled='disabled'],
  &[disabled] {
    opacity: .5;
    pointer-events: none;

    &:hover {
      cursor: default;
    }
  }

background: $colour-btn-cta;
  border: em(1px) solid $colour-btn-cta-text;
  color: $colour-btn-cta-text;

  &:visited {
    color: $colour-btn-cta-text;
  }

  &:hover {
    background: $colour-btn-cta-hover;
    color: $colour-btn-cta-text;
  }

  &:focus {
    background: $colour-btn-cta-hover;
  }

  &:active {
    background: $colour-btn-cta-hover;
  }

background: $colour-btn-secondary;
  border: em(1px) solid $colour-btn-secondary-text;
  color: $colour-btn-secondary-text;

  &:visited {
    color: $colour-btn-secondary-text;
  }

  &:hover {
    background: $colour-btn-secondary-hover;
    color: $colour-btn-secondary-text;
  }

  &:focus {
    background: $colour-btn-secondary-hover;
  }

  &:active {
    background: $colour-btn-secondary-hover;
  }

background: $colour-btn-inverse;
  border: em(1px) solid $colour-btn-inverse-text;
  color: $colour-btn-inverse-text !important; // sass-lint:disable-line no-important

  &:visited {
    color: $colour-btn-inverse-text;
  }

  &:hover {
    background: $colour-btn-inverse-hover;
    color: $colour-btn-inverse-text;
  }

  &:focus {
    background: $colour-btn-inverse-hover;
  }

  &:active {
    background: $colour-btn-inverse-hover;
  }

margin: rem(0 0 $spacing-large 0);
  overflow: hidden;
  position: relative;

  &--flush {
    margin-bottom: 0;
  }

  &--separator,
  .list--unstyled & {
    border-bottom: 1px solid $colour-border;
    margin: rem(0 0 $spacing-small 0);
    padding: rem(0 0 $spacing-x-small 0);
  }

  &__header {
    margin: rem(0 0 $spacing-x-small 0);
  }

  &__heading {
    @include font-size(0);
    font-weight: bold;
    margin: 0;

    a {
      text-decoration: none;

      &:hover {
        text-decoration: underline;
      }

      &:before {
        content: '';
        display: block;
        height: 100%;
        left: 0;
        position: absolute;
        top: 0;
        width: 100%;
        z-index: 0;
      }
    }
  }

  &__author {
    @include font-size(-1);
    color: $colour-nice-dark-grey;
    margin: rem(0 0 $spacing-x-small 0);

    dd {
      display: block;
      margin: 0;
    }
  }

  &__summary {
    margin: rem(0 0 $spacing-x-small 0);
  }

  &__image {
    display: block;
    margin: rem(0 0 $spacing-x-small);
  }

  &__icon {
    margin: rem(0 $spacing-x-small 0 0);
  }

  &__tag {
    @include font-size(-1, true);
  }

  &__metadata {
    @include font-size(-1);
    color: $colour-nice-dark-grey;
    display: inline-block;
    margin: 0;
    position: relative;
    z-index: 1;
  }

  &__metadatum {
    display: block;
    margin: rem(0 $spacing-small $spacing-x-small 0);

    dt,
    dd {
      display: inline-block;
      margin: 0;
    }
  }

  @include mq($from: xs) {
    &__image {
      float: left;
      width: 30%;
    }

    &__text {
      float: left;
      padding-left: rem($spacing-medium);
      width: 70%;
    }
  }

  @include mq($from: sm) {
    &__image {
      width: 20%;
    }

    &__text {
      width: 80%;
    }

    &__metadatum {
      display: inline-block;
      white-space: nowrap;
    }
  }

  @include mq($from: md) {
    &__image {
      width: 30%;
    }

    &__text {
      width: 70%;
    }
  }

  @include mq($from: lg) {
    &__image {
      width: 25%;
    }

    &__text {
      width: 75%;
    }
  }

context.code:
margin: auto;
max-width: rem($nice-container-max-width);
width: 96%; // Fallback for non-calc browsers e.g. IE8, see https://caniuse.com/#feat=calc
width: calc(100% - #{rem($spacing-large)}); // sass-lint:disable-line no-duplicate-properties

context.value:
margin-top: rem($spacing-medium);

  &:first-child {
    margin-top: 0;
  }

  // Heading used for collapsing/expanding the filter group
  &__heading {
    @include font-size(0);
    background: $colour-nice-grey-2;
    border: 0;
    display: block;
    font-weight: bold;
    margin: 0;
    padding: rem($spacing-small);
    position: relative;
    text-align: left;
    width: 100%;

    @include when-is(button) {
      appearance: none;
      cursor: pointer;

      &:after {
        @include nice-icon(chevron-up);
        @include font-size(-1);
        color: $colour-icons;
        line-height: normal; // IE 9/10 bug where rems on block psuedos collapse to 0
        position: absolute;
        right: rem($spacing-small);
        top: rem($spacing-small);
      }

      &[aria-expanded='false']:after {
        content: nice-icon(chevron-down);
      }

      &:hover {
        background: $colour-nice-grey-1;

        // sass-lint:disable no-combinators
        + .filter-group__options {
          border-color: $colour-nice-grey-1;
        }
      }

      &:focus {
        @include default-focus-style;
      }
    }
  }

  // Shows the number of filtered options e.g. "2 selected"
  &__count {
    @include font-size(-1);
    font-weight: normal;
  }

  // Wrapper for the options when filter is a list of checboxes
  &__options {
    border: rem($spacing-x-small) solid $colour-nice-grey-2;

    &[aria-hidden='true'] {
      display: none;
    }
  }

  // Checkbox/radio button option (label)
  &__option {
    background: $colour-nice-white;
    border-top: 1px solid $colour-nice-grey-2;
    cursor: pointer;
    display: block;
    line-height: 1.2;
    padding: rem($spacing-small $spacing-small $spacing-small $spacing-large);

    &:first-child {
      border-top: 0;
    }

    &:hover {
      background: $colour-nice-grey-3;
    }

    input {
      float: left;
      height: rem($spacing-medium);
      margin-left: - rem($spacing-medium + $spacing-small);
      margin-top: rem($spacing-xx-small);
      width: rem($spacing-medium);

      &:focus {
        @include default-focus-style;
      }
    }
  }

@include links-inverse;
  @include font-size(-1);
  background: $colour-panel-inverse;
  color: $colour-text-inverse;
  margin-top: em($spacing-large);
  padding: em($spacing-large 0);

  @media print {
    display: none;
  }

  p {
    @include font-size(-1);
  }

  &__logo {
    display: block;
    margin: em(0 0 $spacing-large 0);
    text-decoration: none !important; // sass-lint:disable-line no-important

    .icon {
      @include font-size(h1);
    }
  }

  &__services {
    margin: em(0 0 $spacing-large 0);
  }

  &__social {
    clear: both;
    margin: em(0 0 $spacing-large 0);
  }

  &__nav {
    clear: both;
    margin: em(0 0 $spacing-large 0);
  }

  &__copyright {
    margin: 0;
    text-align: center;
  }

  @include mq($from: sm) {
    &__logo {
      float: left;
      margin: em(0 $spacing-large $spacing-large 0);
    }

    &__services {
      float: left;
      margin: em(0 0 $spacing-large 0);
    }

    &__social {
      clear: both;
    }

    &__copyright {
      text-align: left;
    }
  }

  @include mq($from: md) {

    &__social {
      clear: none;
      float: right;
    }
  }

&__list {
    list-style: none;
    margin: 0;
    padding: 0;
  }

  &__item {
    border-bottom: 1px solid $colour-border;

    &:first-child {
      border-top: 1px solid $colour-border;
    }

    a {
      @include font-size(-1);
      color: $colour-panel-default;
      display: block;
      padding: em($spacing-small 0);
      text-decoration: none;

      &:hover {
        color: $colour-text-inverse;
      }
    }

    .icon {
      @include font-size(1);
      display: inline-block;
      margin: em(0 $spacing-x-small 0 0);
      vertical-align: middle;
    }
  }

  @include mq($from: sm) {

    &__item {
      border: 0;
      float: left;
      text-align: center;
      width: em(90px);

      &:first-child {
        border: 0;
      }

      a {
        padding: em($spacing-x-small) 0 0;
      }
    }

    .icon {
      @include font-size(h2);
      display: block;
      line-height: 1;
      margin: 0 auto em($spacing-x-small);

      // The following is needed for IE8 to go centrally aligned
      &:before {
        text-align: center;
        width: 100%;
      }
    }
  }

&__heading {
    @include font-size(-1);
    display: inline-block;
    font-weight: normal;
    margin: em(0 $spacing-small 0 0);
    padding: 0;
    vertical-align: top;
  }

  &__list {
    display: inline;
    list-style: none;
    margin: 0;
    padding: 0;
  }

  &__item {
    display: inline-block;

    .icon {
      @include font-size(3);
      line-height: 1;
    }
  }

  a {
    display: block;
    padding: em(0 $spacing-x-small);
    text-decoration: none;
  }

&__list {
    list-style: none;
    margin: 0;
    padding: 0;
  }

  a {
    padding: em($spacing-x-small 0);
    text-decoration: none;
  }

  @include mq($from: sm) {
    &__list {
      line-height: 1;
    }

    &__item {
      border-left: 1px solid $colour-text-inverse;
      display: inline-block;
      line-height: 1;

      &:first-child {
        border-left: 0;

        a {
          padding-left: 0;
        }
      }
    }

    a {
      padding: em(0 $spacing-small);
    }
  }

@include links-inverse;
  background: $colour-hero-background;
  color: $colour-text-inverse;
  margin: rem(0 0 $spacing-medium);
  overflow: hidden;
  //padding: rem(0 0 $spacing-large);

  &__container {
    @extend %container;
  }

  &__body {
    @include clearfix;
    margin-top: rem($spacing-large);
    padding: rem(0 0 $spacing-large);
  }

  // Container for title, lead and actions
  &__copy {
    margin-bottom: rem($spacing-large);
  }

  &__title {
    @include h1;
  }

  &__intro {
    @include lead;
  }

  // Optional footer
  &__footer {
    border-top: rem($spacing-xx-small) solid $colour-hero-border;
    padding: rem($spacing-medium 0);

    p,
    ul {
      margin: 0;
    }
  }

  @include mq($from: md) {
    &__copy {
      float: left;
      margin-bottom: 0;
      width: 55%;
    }

    &__extra {
      float: right;
      width: 35%;
    }
  }

margin: rem(0 0 $spacing-large);

  // The 'on this page' title
  &__title {
    @include font-size(h4);
    margin-top: 0;
  }

  // Lists (both root level and sub level)
  &__list {
    list-style: disc;
    margin-left: em($spacing-medium);

    .in-page-nav__list {
      list-style: circle;
    }

    [aria-hidden='true'] {
      display: none;
    }
  }

  a {
    &:visited {
      color: $colour-link;
    }

    &:focus {
      background: none;
    }
  }

  @include mq($from: md) {
    margin: 0;

    // Docked to the top of the screen
    &--fixed {
      position: fixed;
      top: 0;

      .in-page-nav__title {
        margin-top: rem($spacing-small);
      }
    }

    a {
      &[aria-current='location'] {
        font-weight: bold;
      }
    }
  }

display: block;
  height: 0;
  overflow: hidden;
  padding: 0;
  position: relative;

  &__item,
  iframe,
  embed,
  object,
  video {
    border: 0;
    bottom: 0;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
  }

  // Modifier class for 21:9 aspect ratio
  &--21-9 {
    padding-bottom: percentage(9 / 21);
  }

  // Modifier class for 16:9 aspect ratio
  &--16-9 {
    padding-bottom: percentage(9 / 16);
  }

  // Modifier class for 4:3 aspect ratio
  &--4-3 {
    padding-bottom: percentage(3 / 4);
  }

  // Modifier class for square aspect ratio
  &--square {
    padding-bottom: 100%;
  }

margin: rem(0 0 $spacing-x-large 0);

  &__heading {
    @include font-size(h1);
    margin: rem(0 0 $spacing-small 0);
  }

  &__lead {
    @include font-size(lead);
    margin: rem(0 0 $spacing-small 0);
  }

  &__cta {
    @include font-size(p);
    display: block;
    margin: 0;
  }

  @media print {
    &__cta {
      display: none;
    }
  }

background: $colour-panel-default;
  border: 1px solid $colour-panel-default-border; // To distinguish panel from off-grey body background
  margin: em($spacing-medium 0 $spacing-large);
  padding: em($spacing-medium);

  // sass-lint:disable no-combinators
  > :first-child {
    margin-top: 0;
  }

  // sass-lint:disable no-combinators
  > :last-child {
    margin-bottom: 0;
  }

border-bottom: 1px solid $colour-border;
  display: table;
  margin: em(0 0 $spacing-small);
  max-width: 100%;
  padding: em($spacing-small 0);
  width: 100%;

  &__tag {
    display: table-cell;
    padding: em(0 $spacing-small 0 0);
    vertical-align: top;
  }

  &__label {
    display: table-cell;
    line-height: 1.3;
    vertical-align: middle;
    width: 100%;
  }

@include font-size(0);
  background-color: $colour-panel-inverse;
  border-radius: $nice-tag-border-radius;
  color: $colour-text-inverse;
  display: inline-block;
  font-weight: normal;
  margin: rem(0 0 $spacing-x-small 0);
  padding: em(0 $spacing-x-small);
  position: relative;
  text-decoration: none;
  vertical-align: baseline;

  @include when-is(a) {
    color: $colour-nice-white;
    text-decoration: underline;

    &:visited {
      color: $colour-nice-white;
    }

    &:hover {
      text-decoration: none;
    }

    &:active {
      top: em($spacing-xx-small);
    }
  }

  @media print {
    // sass-lint:disable-block no-important
    background: none !important;
    color: $colour-true-black !important;
    padding: 0 !important;
  }

  &--impact {
    font-weight: 900;
    letter-spacing: 1px;
    padding: em($spacing-xx-small $spacing-small);
    text-transform: uppercase;
  }

  &--super {
    vertical-align: super;
  }

  &--alpha {
    background-color: $colour-alpha;
    color: $colour-text-inverse;
  }

  &--beta {
    background-color: $colour-beta;
    color: $colour-text-inverse;
  }

  &--live {
    background-color: $colour-live;
    color: $colour-text-inverse;
  }

  &--new {
    background: $colour-guidance-new;
  }

  &--updated {
    background: $colour-guidance-updated;
  }

  &--consultation {
    background: $colour-guidance-consultation;
  }

  &--flush {
    margin-bottom: 0;
  }

  &--outline {
    @include font-size(-1);
    background: $colour-nice-grey-3;
    border: 1px solid $colour-border;
    color: $colour-nice-dark-grey;
    position: relative;
  }

  &__remove {
    appearance: none;
    background: 0;
    border: 0;
    color: $colour-nice-teal;
    cursor: pointer;
    margin-left: rem($spacing-x-small);
    padding: 0;
    text-decoration: none;

    &:focus {
      @include default-focus-style;
    }

    &:visited {
      color: $colour-nice-teal;
    }

    &:hover,
    &:active {
      color: $colour-text;
    }

    &:hover:after {
      background: rgba($colour-nice-black, .1);
    }

    &:after {
      border-radius: $nice-tag-border-radius;
      content: '';
      display: block;
      height: 100%;
      left: 0;
      position: absolute;
      top: 0;
      width: 100%;
    }
  }

@include clearfix;
  margin: rem(0 0 $spacing-medium);

  &__count {
    @include font-size(h5);
    font-weight: bold;
    margin: rem(0 0 $spacing-small);
  }

  &__sort {
    margin: 0;
  }

  &__filters {
    clear: both;
    list-style: none;
    margin: 0;
    padding-top: rem($spacing-small);
  }

  @include mq($from: sm) {
    margin: rem(0 0 $spacing-medium);

    &__count {
      float: left;
      margin: 0;
      width: 50%;
    }

    &__sort {
      float: right;
      text-align: right;
      width: 50%;
    }

    &__filter {
      display: inline-block;
      margin-right: rem($spacing-x-small);
    }
  }

background: $colour-nice-white;
  border: 1px solid $colour-border;
  margin: em(0 0 $spacing-large);

  a {
    color: $colour-link;
    display: block;
    padding: em($spacing-small);
    position: relative;
    text-decoration: none;

    &[aria-current='page'] {
      background: $colour-nice-grey-3;
      font-weight: bold;

      &:before {
        background: $colour-icons;
        content: '';
        display: block;
        height: 100%;
        left: 0;
        position: absolute;
        top: 0;
        width: em($spacing-x-small);
      }
    }

    &:hover {
      background: $colour-nice-grey-2;
    }

    &:active {
      background: $colour-icons;
      color: $colour-link-inverse;
    }
  }

  // The title nav link
  &__root {
    @include font-size(h6);
    border-bottom: 1px solid $colour-border;
    font-weight: bold;
    margin: 0;

    a {
      color: $colour-text;
    }
  }

  &__list {
    list-style: none;
    margin: 0;
    padding: 0;
  }

  &__list-item {
    border-top: 1px solid $colour-border;
  }

  @media print {
    display: none;
  }

margin: em($spacing-medium 0 $spacing-large);

  // The list of tabs
  &__list {
    display: none;
    list-style: none;
    margin: 0;
    padding: 0;
    position: relative;
    top: 1px; // To cover the border around the tabs content

    .js & {
      display: block;
    }
  }

  // Individual tab
  &__tab {
    float: left;
  }

  // The button/link for the tab itself
  &__tab-btn {
    @include link-default;
    background: 0;
    border: 0;
    border-left: 1px solid transparent;
    border-right: 1px solid transparent;
    cursor: pointer;
    display: block;
    margin: 0;
    padding: em($spacing-medium $spacing-medium);
    position: relative;

    &:focus {
      // Stop focus ring clipping
      z-index: 1;
    }

    &[aria-selected='true'] {
      background: $colour-panel-default;
      border-left: 1px solid $colour-border;
      border-right: 1px solid $colour-border;
      color: $colour-text;
      text-decoration: none;

      &:before {
        // The -1px is to over the grey border round the tab
        background: $colour-icons;
        content: '';
        height: em($spacing-x-small);
        left: -1px;
        position: absolute;
        right: -1px;
        top: 0;
      }
    }
  }

  // The tab content containing the tab panels
  &__content {
    clear: both;

    .js & {
      background: $colour-panel-default;
      border: 1px solid $colour-border;
      clear: both;
      padding: em($spacing-medium);
    }
  }

  // An individual tab content pane
  &__pane {
    margin-bottom: em($spacing-medium);

    // Assume tabs will stack if no JS is available
    .js & {
      display: none;
      margin-bottom: 0;

      &--active {
        display: block;
      }
    }

    :first-child {
      margin-top: 0;
    }

    :last-child {
      margin-bottom: 0;
    }
  }

