.test {
  @include grid($compact: true);

  @include element('item') {
    @include grid-item;
    background: red;
    width: 100%;
  }

  @include mq($from: md) {
    @include element('item') {
      background: green;
      width: percentage(3 / 12);
    }
  }
}

