letter-spacing: -.31em; // Remove whitespace caused by `inline-block` elements
list-style: none; // Allow the grid system to be used on lists.
margin: 0; // Remove any margins that might affect the grid system.
margin-left: -$nice-grid-gutter; // Apply a negative `margin-left` to negate the columns gutters.
padding: 0; // Remove any paddings that might affect the grid system.

@if $reverse {
  @include grid-reverse;
}

@if $gutterless {
  @include grid-gutterless;
}

@if $compact {
  @include grid-compact;
}

@if $loose {
  @include grid-loose;
}

@if $right {
  @include grid-right;
}

@if $center {
  @include grid-center;
}

@if $debug {
  @include grid-debug;
}

@include border-box;
direction: ltr;
display: inline-block;
letter-spacing: normal;
padding-left: $nice-grid-gutter;
position: relative;
text-align: left;
vertical-align: top;
width: percentage($columns / 12);
word-spacing: normal;

@if $push {
  left: percentage($push / 12);
}

@if $pull {
  right: percentage($pull / 12);
}

@if $gutterless {
  @include grid-item-gutterless;
}

@if $compact {
  @include grid-item-compact;
}

@if $loose {
  @include grid-item-loose;
}

@if $middle {
  @include grid-item-middle;
}

@if $bottom {
  @include grid-item-bottom;
}

@include _grid-item-breakpoint($xs, xs);
@include _grid-item-breakpoint($sm, sm);
@include _grid-item-breakpoint($md, md);
@include _grid-item-breakpoint($lg, lg);
@include _grid-item-breakpoint($xl, xl);

margin: auto;
max-width: rem($nice-container-max-width);
width: 96%; // Fallback for non-calc browsers e.g. IE8, see https://caniuse.com/#feat=calc
width: calc(100% - #{rem($spacing-large)}); // sass-lint:disable-line no-duplicate-properties

@if map-has-key($scales, $scale) {
  @return map-get($scales, $scale);
} @else {
  @error 'Scale with name #{$scale} could not be found in the $scales map';
}
