  }

border-bottom: 1px solid $colour-border;
  display: table;
  margin: em(0 0 $spacing-medium);
  padding: em($spacing-small 0);
  width: 100%;

  &__tag {
    display: table-cell;
    padding: em(0 $spacing-small 0 0);
  }

  &__label {
    display: table-cell;
    line-height: 1.3;
    vertical-align: middle;
    width: 100%;
  }

margin: em($spacing-medium 0 $spacing-large);

  // The list of tabs
  &__list {
    list-style: none;
    margin: 0;
    overflow: hidden;
    padding: 0;
    position: relative;
    top: 1px; // To cover the border around the tabs content
  }

  // Individual tab
  &__tab {
    float: left;

    &--active .tabs__tab-btn { // sass-lint:disable-line no-combinators
      background: $colour-panel-default;
      border-left: 1px solid $colour-border;
      border-right: 1px solid $colour-border;
      border-top: 1px solid $colour-border;
      box-shadow: inset 0 em($spacing-x-small) 0 0 $colour-nice-teal;
      color: $colour-text;
      text-decoration: none;
    }
  }

  // The button/link for the tab itself
  &__tab-btn {
    @extend %link;
    background: 0;
    border: 0;
    border-left: 1px solid transparent;
    border-right: 1px solid transparent;
    border-top: 1px solid transparent;
    cursor: pointer;
    display: block;
    margin: 0;
    padding: em($spacing-medium $spacing-medium);
  }

  // The tab content containing the tab panels
  &__content {
    background: $colour-panel-default;
    border: 1px solid $colour-border;
    padding: em($spacing-medium);
  }

  // An individual tab content pane
  &__pane {

    // Assume tabs will stack if no JS is available
    .js & {
      display: none;

      &--active {
        display: block;
      }
    }

    :first-child {
      margin-top: 0;
    }

    :last-child {
      margin-bottom: 0;
    }
  }

@include font-size(0);
  list-style: none;
  margin: em($spacing-small 0 $spacing-large);
  padding: 0;

  &__crumb {
    display: inline-block;

    &:before {
      content: get-glyph(heavy-right-pointing-angle-quotation-mark-ornament);
      font-size: 70%;
      margin: em(0 $spacing-x-small);
    }

    &:first-child:before {
      display: none;
    }
  }

  a {
    color: $colour-text;
    text-decoration: underline;

    &:hover {
      text-decoration: none;
    }

    &:focus {
      color: $colour-link-focus-text;
    }

    &:active {
      color: $colour-link-active;
    }
  }

// Repeat selector for specificity to avoid link overrides
  @include remove-mz-focus-inner;
  @include font-size(0);
  appearance: none;
  background: $colour-btn-primary;
  border: em(1px) solid $colour-btn-primary-text;
  border-radius: 0;
  color: $colour-btn-primary-text;
  cursor: pointer;
  display: inline-block;
  margin: em(0 $spacing-small $spacing-small -4px); // Negative left margin to combat inline-block gaps
  padding: em($spacing-small $spacing-medium);
  position: relative;
  text-decoration: none;
  vertical-align: top;

  &:first-child {
    margin-left: 0;
  }

  &:visited {
    color: $colour-btn-primary-text;
  }

  &:hover {
    background: $colour-btn-primary-hover;
    color: $colour-btn-primary-text;
    text-decoration: none;
  }

  &:focus {
    @include default-focus-style;
    background: $colour-btn-primary;
    border-color: $colour-focus;
  }

  &:active {
    background: $colour-btn-primary-hover;
    text-decoration: none;
    top: em(2px);
  }

  // Disabled button styles
  &--disabled,
  &.disabled,
  &[aria-disabled='true'],
  &[disabled='disabled'],
  &[disabled] {
    opacity: .5;
    pointer-events: none;

    &:hover {
      cursor: default;
    }
  }

background: $colour-btn-cta;
  border: em(1px) solid $colour-btn-cta-text;
  color: $colour-btn-cta-text;

