context.code:
margin: auto;
max-width: rem(1170px);
width: 96%;

context.value:
display: block;
  height: 0;
  overflow: hidden;
  padding: 0;
  position: relative;

  &__item,
  iframe,
  embed,
  object,
  video {
    border: 0;
    bottom: 0;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
  }

  // Modifier class for 21:9 aspect ratio
  &--21-9 {
    padding-bottom: percentage(9 / 21);
  }

  // Modifier class for 16:9 aspect ratio
  &--16-9 {
    padding-bottom: percentage(9 / 16);
  }

  // Modifier class for 4:3 aspect ratio
  &--4-3 {
    padding-bottom: percentage(3 / 4);
  }

  // Modifier class for square aspect ratio
  &--square {
    padding-bottom: 100%;
  }

background: $colour-panel-default;
  margin: em($spacing-medium 0 $spacing-large);
  padding: em($spacing-medium);

  :first-child {
    margin-top: 0;
  }

  :last-child {
    margin-bottom: 0;
  }

context.code:
letter-spacing: -.31em; // Remove whitespace caused by `inline-block` elements
list-style: none; // Allow the grid system to be used on lists.
margin: 0; // Remove any margins that might affect the grid system.
margin-left: -$nice-grid-gutter; // Apply a negative `margin-left` to negate the columns gutters.
padding: 0; // Remove any paddings that might affect the grid system.

@if $reverse {
  @include grid-reverse;
}

@if $gutterless {
  @include grid-gutterless;
}

@if $compact {
  @include grid-compact;
}

@if $loose {
  @include grid-loose;
}

@if $right {
  @include grid-right;
}

@if $center {
  @include grid-center;
}

@if $debug {
  @include grid-debug;
}

example.code:
.test {
  @include grid($compact: true);

  @include element('item') {
    @include grid-item;
    background: red;
    width: 100%;
  }

  @include mq($from: md) {
    @include element('item') {
      background: green;
      width: percentage(3 / 12);
    }
  }
}

